
export type MicroMatcher = { kind: string } | String

export const Or = function (alternatives: MicroMatcher[]): MicroMatcher {
    let or = {
        kind: "or",
        components: alternatives
    };
    return or;
};

export const Regex = function (javaStyleRegex: string): MicroMatcher {
    let regex = { kind: "regex", regularExpression: javaStyleRegex };
    return regex;
}
